<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class User_model extends CI_Model {        public function __construct()    {        parent::__construct();        $this->load->database();    }            public function user_insert($user)    {        $this->db->insert('user',$user);    }            public function join_lists()    {        $this->db->select('*');        $this->db->from('news');        $this->db->join('user','user.user_id = news.user_id','left');        $query = $this->db->get();        return $query;    }            public function user_lists()    {        $this->db->select('*');        $this->db->from('user');        $query = $this->db->get();        return $query;    }            public function user_edit($user_id='')    {        $edit = $this->db->query("select * from user where user_id='$user_id'");        return $edit;    }            public function user_update($data=array())    {        $this->db->where('user_id',$data['user_id']);        $this->db->update('user',$data);    }    //user_delete    public function user_delete($user_id='')    {           $this->db->where('user_id',$user_id);        $this->db->delete('user');    }    }